let hasDownloaded = false;

const handleDownload = () => {
  // Get the download button element
  const downloadButton = document.getElementById('downloadButton');
  
  // Disable the button for 10 seconds
  downloadButton.disabled = true;

  if (!Resume) {
    toast.error('Resume file is not available!', {
      autoClose: 3000,
      closeOnClick: false,
      pauseOnHover: false,
      draggable: false,
      progress: undefined,
    });
    // Re-enable button after 10 seconds
    setTimeout(() => {
      downloadButton.disabled = false;
    }, 10000);
    return;
  }

  // If the resume has already been downloaded, show a warning and stop further downloads
  if (hasDownloaded) {
    toast.warning('You have already downloaded the resume!', {
      autoClose: 3000,
      closeOnClick: false,
      pauseOnHover: false,
      draggable: false,
      progress: undefined,
    });
    // Re-enable button after 10 seconds
    setTimeout(() => {
      downloadButton.disabled = false;
    }, 10000);
    return;
  }

  // Show info message about preparation
  toast.info('Preparing to download my resume', {
    autoClose: 3500,
    closeOnClick: false,
    pauseOnHover: false,
    draggable: false,
    progress: undefined,
  });

  // Allow only one download per session
  hasDownloaded = true;

  // Show success message and trigger the download after 3475ms (preserving your original timing)
  setTimeout(() => {
    toast.success('Resume has been successfully downloaded in your device !', {
      autoClose: 3500,
      closeOnClick: false,
      pauseOnHover: false,
      draggable: false,
      progress: undefined,
    });
  }, 3475);

  // Trigger the download after 6500ms (preserving your original timing)
  setTimeout(() => {
    const link = document.createElement('a');
    link.href = Resume;
    link.download = 'Resume.pdf';
    link.click();
  }, 6500);

  // Re-enable the button after 10 seconds
  setTimeout(() => {
    downloadButton.disabled = false;
  }, 10000);
};


  return (
    <>
      <div id="About" className="Abt">
        <h2 style={{ textAlign: 'center' }}>About</h2>
        <div className="About" id="Abt">
          <figure>
            <img src={Allwin} alt="Allwin E K" onContextMenu={(e) => e.preventDefault()} draggable="false" style={{ userSelect: 'none' }} />
            <figcaption>I am <b>Allwin E K</b>, a passionate Web Developer. I have a solid foundation in <b>HTML, CSS and JavaScript</b>, and I love creating user-friendly and responsive web interfaces. I'm currently pursuing a <b>B.E</b> in <b>Computer Science and Engineering</b> at <b>Ponjesly College of Engineering, Nagercoil</b>.
              <br /><br />
              I strongly believe in continuous learning and improving myself, so I try my best to learn in any situation possible, unfavorable or not.
            </figcaption>
            <button className="gradient-button" id="downloadButton" style={{ fontSize: '20px' }} onClick={handleDownload}>Download Resume</button>
          </figure>
        </div>
      </div>
    </>
  )
}
export default About;
